// src/App.js
import React, { useState, useEffect } from 'react';
import './App.css';

const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000';

function App() {
  const [garden, setGarden] = useState([]);
  const [chatMessages, setChatMessages] = useState([]);
  const [inputMessage, setInputMessage] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [showBee, setShowBee] = useState(false);
  const [beeMessage, setBeeMessage] = useState('');
  const userId = 'default'; // In production, use actual user authentication

  // Plant icons for different task sizes
  const plantIcons = {
    small: '🌱',
    medium: '🌿',
    big: '🌳'
  };

  // Load garden and chat history on component mount
  useEffect(() => {
    loadGarden();
    loadChatHistory();
  }, []);

  const loadGarden = async () => {
    try {
      const response = await fetch(`${API_BASE_URL}/api/garden/${userId}`);
      const data = await response.json();
      setGarden(data.garden);
    } catch (error) {
      console.error('Error loading garden:', error);
    }
  };

  const loadChatHistory = async () => {
    try {
      const response = await fetch(`${API_BASE_URL}/api/chat-history/${userId}`);
      const data = await response.json();
      setChatMessages(data.history);
    } catch (error) {
      console.error('Error loading chat history:', error);
    }
  };

  const showBeeCompanion = (message) => {
    setBeeMessage(message);
    setShowBee(true);
    setTimeout(() => setShowBee(false), 4000);
  };

  const plantTask = async (taskSize) => {
    try {
      const response = await fetch(`${API_BASE_URL}/api/garden/${userId}/plant`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ taskSize }),
      });

      const data = await response.json();
      setGarden(data.garden);
      showBeeCompanion(`Wonderful! You planted a ${taskSize} task! 🌱`);
    } catch (error) {
      console.error('Error planting task:', error);
    }
  };

  const waterGarden = () => {
    showBeeCompanion("Your garden is well-watered and thriving! 💧🌿");
  };

  const sendMessageToAI = async (message) => {
    setIsLoading(true);

    try {
      const response = await fetch(`${API_BASE_URL}/api/chat`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ message, userId }),
      });

      const data = await response.json();

      if (data.response) {
        setChatMessages(prev => [...prev, 
          { role: 'user', content: message },
          { role: 'assistant', content: data.response }
        ]);
      } else if (data.message) {
        setChatMessages(prev => [...prev,
          { role: 'user', content: message },
          { role: 'assistant', content: data.message }
        ]);
      }
    } catch (error) {
      console.error('Error sending message:', error);
      setChatMessages(prev => [...prev,
        { role: 'user', content: message },
        { role: 'assistant', content: "I'm having trouble connecting right now, but I'm here to help! Try asking me again in a moment. 🐝" }
      ]);
    } finally {
      setIsLoading(false);
    }
  };

  const handleSendMessage = () => {
    if (inputMessage.trim()) {
      sendMessageToAI(inputMessage);
      setInputMessage('');
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      handleSendMessage();
    }
  };

  return (
    <div className="min-h-screen bg-gray-100 p-4">
      <div className="max-w-4xl mx-auto">
        {/* Header */}
        <div className="bg-white rounded-lg shadow-md p-6 mb-6">
          <h1 className="text-4xl font-bold text-gray-800 mb-4">Prototype 3</h1>
          <p className="text-gray-600 text-lg leading-relaxed">
            In this prototype, you "plant" tasks of different sizes (small, medium, big) and completing them 
            helps grow a visual garden, rewarding you for your progress. You have a text bar to ask for help that 
            can also serve as a reminder system that can check in on you every so often, and a bee gardener 
            companion who will offer compliments on the garden, provide support and check ins.
          </p>
        </div>

        {/* Main Garden Interface */}
        <div className="bg-green-400 rounded-lg shadow-md p-6 relative overflow-hidden">
          {/* Task Planting Section */}
          <div className="mb-8">
            <h2 className="text-white text-xl font-semibold mb-4">Start new task</h2>
            <div className="flex space-x-4">
              <button
                onClick={() => plantTask('small')}
                className="bg-green-500 hover:bg-green-600 text-white p-4 rounded-lg flex flex-col items-center transition-colors"
              >
                <span className="text-2xl mb-2">🌱</span>
                <span>Small task</span>
              </button>
              <button
                onClick={() => plantTask('medium')}
                className="bg-green-500 hover:bg-green-600 text-white p-4 rounded-lg flex flex-col items-center transition-colors"
              >
                <span className="text-2xl mb-2">🌿</span>
                <span>Medium Task</span>
              </button>
              <button
                onClick={() => plantTask('big')}
                className="bg-green-500 hover:bg-green-600 text-white p-4 rounded-lg flex flex-col items-center transition-colors"
              >
                <span className="text-2xl mb-2">🌳</span>
                <span>Big task</span>
              </button>
            </div>
          </div>

          {/* Garden Display */}
          <div className="mb-8">
            <h3 className="text-white text-lg font-semibold mb-4">Today's garden</h3>
            <div className="bg-green-300 rounded-lg p-4 min-h-32 flex flex-wrap gap-2">
              {garden.map((plant) => (
                <span key={plant.id} className="text-3xl animate-pulse">
                  {plantIcons[plant.type]}
                </span>
              ))}
            </div>
          </div>

          {/* Bee Companion */}
          <div className="absolute top-4 right-4">
            <div className="text-4xl animate-bounce">🐝</div>
            {showBee && (
              <div className="absolute top-12 right-0 bg-yellow-200 text-gray-800 p-3 rounded-lg shadow-lg max-w-xs animate-fade-in">
                <p className="text-sm">{beeMessage}</p>
              </div>
            )}
          </div>

          {/* Bottom Section */}
          <div className="flex justify-between items-end">
            {/* Chat Interface */}
            <div className="flex-1 mr-4">
              <div className="bg-gray-800 text-white p-4 rounded-lg mb-2 max-h-32 overflow-y-auto">
                {chatMessages.length === 0 ? (
                  <p className="text-gray-300">Let me know if you need any help with any tasks</p>
                ) : (
                  <div className="space-y-2">
                    {chatMessages.slice(-4).map((msg, index) => (
                      <div key={index} className={`text-sm ${msg.role === 'user' ? 'text-blue-300' : 'text-green-300'}`}>
                        <strong>{msg.role === 'user' ? 'You' : 'Bee Helper'}:</strong> {msg.content}
                      </div>
                    ))}
                  </div>
                )}
              </div>
              <div className="flex">
                <input
                  type="text"
                  value={inputMessage}
                  onChange={(e) => setInputMessage(e.target.value)}
                  onKeyPress={handleKeyPress}
                  placeholder="Text bar"
                  className="flex-1 p-3 bg-gray-600 text-white rounded-l-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                  disabled={isLoading}
                />
                <button
                  onClick={handleSendMessage}
                  disabled={isLoading || !inputMessage.trim()}
                  className="bg-blue-600 hover:bg-blue-700 disabled:bg-gray-500 text-white px-6 py-3 rounded-r-lg transition-colors"
                >
                  {isLoading ? '...' : 'Send'}
                </button>
              </div>
            </div>

            {/* Water Garden Button */}
            <button
              onClick={waterGarden}
              className="bg-blue-500 hover:bg-blue-600 text-white px-6 py-3 rounded-lg transition-colors"
            >
              Click to water garden
            </button>
          </div>

          {/* Bee Companion Info */}
          <div className="absolute bottom-20 left-1/2 transform -translate-x-1/2 text-center">
            <p className="text-green-800 text-sm bg-green-200 px-4 py-2 rounded-lg">
              Bee gardener companion appears when planting to compliment your garden, provide support and check in
            </p>
          </div>
        </div>

        {/* Chat History */}
        {chatMessages.length > 0 && (
          <div className="mt-6 bg-white rounded-lg shadow-md p-6">
            <h3 className="text-lg font-semibold mb-4 text-gray-800">Chat History</h3>
            <div className="space-y-3 max-h-64 overflow-y-auto">
              {chatMessages.map((msg, index) => (
                <div key={index} className={`p-3 rounded-lg ${
                  msg.role === 'user' 
                    ? 'bg-blue-100 text-blue-800 ml-8' 
                    : 'bg-green-100 text-green-800 mr-8'
                }`}>
                  <div className="font-semibold text-sm mb-1">
                    {msg.role === 'user' ? 'You' : 'Bee Helper 🐝'}
                  </div>
                  <div>{msg.content}</div>
                </div>
              ))}
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
