// src/App.js
import React, { useState, useEffect, useRef } from 'react';
import './App.css';

const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000';

function App() {
  const [garden, setGarden] = useState([]);
  const [chatMessages, setChatMessages] = useState([]);
  const [inputMessage, setInputMessage] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [showBee, setShowBee] = useState(false);
  const [beeMessage, setBeeMessage] = useState('');
  const [showTaskModal, setShowTaskModal] = useState(false);
  const [pendingTaskSize, setPendingTaskSize] = useState('');
  const [taskDescription, setTaskDescription] = useState('');
  const [chatMode, setChatMode] = useState('assistant'); // 'assistant' or 'gardener'
  const [showGardener, setShowGardener] = useState(true);
  const [gardenerPosition, setGardenerPosition] = useState({ x: 75, y: 80 });
  const [gardenerEmotion, setGardenerEmotion] = useState('happy');
  const userId = 'default';
  const [beePosition, setBeePosition] = useState({ x: 20, y: 20 });
  const [beeDirection, setBeeDirection] = useState(1);
  const [beeEmotion, setBeeEmotion] = useState('happy');
  const [lastGardenSize, setLastGardenSize] = useState(0);
  const [hoveredPlant, setHoveredPlant] = useState(null);
  const checkInTimerRef = useRef(null);
  const lastCheckInRef = useRef(Date.now());
  const beeTimerRef = useRef(null);
  const beeEmotionTimerRef = useRef(null);
  const gardenerTimerRef = useRef(null);

  // Status colors and labels
  const statusConfig = {
    todo: { color: 'border-gray-300 bg-gray-50', label: 'To Do', emoji: '📝' },
    inProgress: { color: 'border-yellow-300 bg-yellow-50', label: 'In Progress', emoji: '🔄' },
    completed: { color: 'border-green-300 bg-green-50', label: 'Completed', emoji: '✅' }
  };

  // Gardener character emotions and sprites
  const gardenerEmojis = {
    happy: '👨‍🌾😊',
    excited: '👨‍🌾🎉',
    working: '👨‍🌾💪',
    thinking: '👨‍🌾🤔',
    proud: '👨‍🌾😌'
  };

  // Bee animation and behavior
  useEffect(() => {
    beeTimerRef.current = setInterval(() => {
      setBeePosition(prev => {
        let newX = prev.x + (beeDirection * 1.5);
        const newY = prev.y + (Math.sin(Date.now() * 0.005) * 0.8);

        if (newX > 85) {
          setBeeDirection(-1);
          newX = 85;
        } else if (newX < 5) {
          setBeeDirection(1);
          newX = 5;
        }

        return { 
          x: newX, 
          y: Math.max(55, Math.min(75, newY))
        };
      });
    }, 150);

    // Gardener gentle movement
    gardenerTimerRef.current = setInterval(() => {
      setGardenerPosition(prev => ({
        x: Math.max(70, Math.min(85, prev.x + (Math.random() - 0.5) * 2)),
        y: Math.max(75, Math.min(85, prev.y + (Math.random() - 0.5) * 1))
      }));
    }, 3000);

    beeEmotionTimerRef.current = setInterval(() => {
      if (garden.length > lastGardenSize) {
        setBeeEmotion('excited');
        setGardenerEmotion('excited');
        const compliments = [
          "Great work! 🌟",
          "Your garden is thriving! 🌸",
          "Beautiful growth! 🌱",
          "Keep it up! ✨"
        ];
        showBeeCompanion(compliments[Math.floor(Math.random() * compliments.length)]);
        setLastGardenSize(garden.length);
        setTimeout(() => {
          setBeeEmotion('happy');
          setGardenerEmotion('happy');
        }, 3000);
      }
    }, 10000);

    return () => {
      if (beeTimerRef.current) clearInterval(beeTimerRef.current);
      if (beeEmotionTimerRef.current) clearInterval(beeEmotionTimerRef.current);
      if (gardenerTimerRef.current) clearInterval(gardenerTimerRef.current);
    };
  }, [garden.length, lastGardenSize, beeDirection]);

  const getBeeEmoji = () => {
    switch (beeEmotion) {
      case 'excited': return '🐝✨';
      case 'sad': return '🐝😔';
      case 'content': return '🐝💭';
      default: return '🐝';
    }
  };

  // Load garden and chat history on component mount
  useEffect(() => {
    loadGarden();
    loadChatHistory();
    startCheckInTimer();

    return () => {
      if (checkInTimerRef.current) {
        clearInterval(checkInTimerRef.current);
      }
    };
  }, []);

  const startCheckInTimer = () => {
    checkInTimerRef.current = setInterval(() => {
      const timeSinceLastMessage = Date.now() - lastCheckInRef.current;
      if (timeSinceLastMessage > 300000) { // 5 minutes
        initiateCheckIn();
      }
    }, 300000);
  };

  const initiateCheckIn = () => {
    const checkInMessages = chatMode === 'gardener' ? [
      "How's the garden growing today?",
      "Need any help tending to your tasks?",
      "Ready to plant something new?",
      "How are your tasks taking root?",
      "Time to water the garden with some progress?"
    ] : [
      "How's your progress?",
      "Need help with anything?",
      "How are you feeling about your tasks?",
      "Want to tackle something new?",
      "How's your focus today?"
    ];

    const randomMessage = checkInMessages[Math.floor(Math.random() * checkInMessages.length)];

    setChatMessages(prev => [...prev, {
      role: chatMode === 'gardener' ? 'gardener' : 'assistant',
      content: randomMessage,
      isCheckIn: true
    }]);

    lastCheckInRef.current = Date.now();
  };

  const loadGarden = async () => {
    try {
      const response = await fetch(`${API_BASE_URL}/api/garden/${userId}`);
      const data = await response.json();
      setGarden(data.garden);
      setLastGardenSize(data.garden.length);
    } catch (error) {
      console.error('Error loading garden:', error);
    }
  };

  const loadChatHistory = async () => {
    try {
      const response = await fetch(`${API_BASE_URL}/api/chat-history/${userId}`);
      const data = await response.json();
      setChatMessages(data.history);
    } catch (error) {
      console.error('Error loading chat history:', error);
    }
  };

  const showBeeCompanion = (message) => {
    setBeeMessage(message);
    setShowBee(true);
    setTimeout(() => setShowBee(false), 3000);
  };

  const handleTaskButtonClick = (taskSize) => {
    setPendingTaskSize(taskSize);
    setShowTaskModal(true);
  };

  const plantTask = async (taskSize, taskDescription = '', status = 'todo') => {
    try {
      const response = await fetch(`${API_BASE_URL}/api/garden/${userId}/plant`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ 
          taskSize, 
          description: taskDescription || `${taskSize} task`,
          status
        }),
      });

      const data = await response.json();
      setGarden(data.garden);

      const statusText = status === 'completed' ? 'completed' : 
                        status === 'inProgress' ? 'in-progress' : 'new';
      showBeeCompanion(`Added "${taskDescription || `${taskSize} task`}" as ${statusText}!`);
    } catch (error) {
      console.error('Error planting task:', error);
    }
  };

  const updateTaskStatus = async (taskId, newStatus) => {
    try {
      const response = await fetch(`${API_BASE_URL}/api/garden/${userId}/task/${taskId}/status`, {
        method: 'PATCH',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ status: newStatus }),
      });

      const data = await response.json();
      setGarden(data.garden);

      const task = data.garden.find(t => t.id == taskId);
      if (task) {
        const emoji = newStatus === 'completed' ? '🎉' : newStatus === 'inProgress' ? '🔄' : '📝';
        showBeeCompanion(`${emoji} "${task.description}" is now ${statusConfig[newStatus].label}!`);

        if (newStatus === 'completed') {
          setGardenerEmotion('proud');
          setTimeout(() => setGardenerEmotion('happy'), 3000);
        }
      }
    } catch (error) {
      console.error('Error updating task status:', error);
    }
  };

  const handleModalSubmit = () => {
    if (taskDescription.trim()) {
      plantTask(pendingTaskSize, taskDescription.trim());
    }
    setShowTaskModal(false);
    setTaskDescription('');
    setPendingTaskSize('');
  };

  const handleModalCancel = () => {
    setShowTaskModal(false);
    setTaskDescription('');
    setPendingTaskSize('');
  };

  const sendMessageToAI = async (message) => {
    setIsLoading(true);
    lastCheckInRef.current = Date.now();

    try {
      const response = await fetch(`${API_BASE_URL}/api/chat`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ 
          message, 
          userId,
          isGardener: chatMode === 'gardener'
        }),
      });

      const data = await response.json();

      setChatMessages(prev => [...prev, 
        { role: 'user', content: message },
        { role: chatMode === 'gardener' ? 'gardener' : 'assistant', content: data.response || data.message }
      ]);

      // Update garden if tasks were added
      if (data.plantAdded && data.garden) {
        const oldGardenSize = garden.length;
        setGarden(data.garden);
        setLastGardenSize(oldGardenSize);

        if (data.plant) {
          const emoji = data.plant.status === 'completed' ? '🎉' : 
                       data.plant.status === 'inProgress' ? '🔄' : '✨';
          setTimeout(() => {
            showBeeCompanion(`${emoji} Added "${data.plant.description}" to your garden!`);
          }, 500);

          setGardenerEmotion('working');
          setTimeout(() => setGardenerEmotion('happy'), 2000);
        }
      }

      if (data.garden && !data.plantAdded) {
        setGarden(data.garden);
      }

    } catch (error) {
      console.error('Error sending message:', error);
      setChatMessages(prev => [...prev,
        { role: 'user', content: message },
        { role: chatMode === 'gardener' ? 'gardener' : 'assistant', content: "I'm having trouble right now. Try again in a moment." }
      ]);
    } finally {
      setIsLoading(false);
    }
  };

  const handleSendMessage = () => {
    if (inputMessage.trim()) {
      sendMessageToAI(inputMessage);
      setInputMessage('');
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      handleSendMessage();
    }
  };

  const toggleChatMode = () => {
    setChatMode(prev => prev === 'assistant' ? 'gardener' : 'assistant');
    setGardenerEmotion('happy');
  };

  const getStatusCounts = () => {
    return {
      todo: garden.filter(t => t.status === 'todo').length,
      inProgress: garden.filter(t => t.status === 'inProgress').length,
      completed: garden.filter(t => t.status === 'completed').length
    };
  };

  const statusCounts = getStatusCounts();

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 p-4">
      <div className="max-w-4xl mx-auto">
        {/* Header */}
        <div className="bg-white rounded-xl shadow-lg p-6 mb-6 border border-gray-200">
          <div className="flex justify-between items-start">
            <div>
              <h1 className="text-3xl font-bold text-gray-900 mb-3">Task Garden</h1>
              <p className="text-gray-600 leading-relaxed">
                Plant and tend to your tasks with Harold the gardener. 
                Watch your productivity bloom as you complete your goals!
              </p>
            </div>
            <div className="flex gap-2">
              <button
                onClick={toggleChatMode}
                className={`px-4 py-2 rounded-lg font-medium transition-colors ${
                  chatMode === 'gardener' 
                    ? 'bg-green-500 text-white' 
                    : 'bg-gray-200 text-gray-700 hover:bg-gray-300'
                }`}
              >
                👨‍🌾 Harold
              </button>
              <button
                onClick={toggleChatMode}
                className={`px-4 py-2 rounded-lg font-medium transition-colors ${
                  chatMode === 'assistant' 
                    ? 'bg-blue-500 text-white' 
                    : 'bg-gray-200 text-gray-700 hover:bg-gray-300'
                }`}
              >
                🤖 Assistant
              </button>
            </div>
          </div>

          {/* Status Summary */}
          <div className="flex gap-4 mt-4 text-sm">
            <div className="flex items-center gap-1">
              <span className="w-3 h-3 bg-gray-300 rounded-full"></span>
              <span>To Do: {statusCounts.todo}</span>
            </div>
            <div className="flex items-center gap-1">
              <span className="w-3 h-3 bg-yellow-300 rounded-full"></span>
              <span>In Progress: {statusCounts.inProgress}</span>
            </div>
            <div className="flex items-center gap-1">
              <span className="w-3 h-3 bg-green-300 rounded-full"></span>
              <span>Completed: {statusCounts.completed}</span>
            </div>
          </div>
        </div>

        {/* Task Modal */}
        {showTaskModal && (
          <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
            <div className="bg-white rounded-xl p-6 max-w-md w-full mx-4">
              <h3 className="text-lg font-semibold mb-4">Add {pendingTaskSize} Task</h3>
              <input
                type="text"
                value={taskDescription}
                onChange={(e) => setTaskDescription(e.target.value)}
                placeholder="What task would you like to add?"
                className="w-full p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 mb-4"
                autoFocus
                onKeyPress={(e) => e.key === 'Enter' && handleModalSubmit()}
              />
              <div className="flex gap-2 justify-end">
                <button
                  onClick={handleModalCancel}
                  className="px-4 py-2 text-gray-600 hover:bg-gray-100 rounded-lg"
                >
                  Cancel
                </button>
                <button
                  onClick={handleModalSubmit}
                  disabled={!taskDescription.trim()}
                  className="px-4 py-2 bg-blue-500 hover:bg-blue-600 disabled:bg-gray-400 text-white rounded-lg"
                >
                  Add Task
                </button>
              </div>
            </div>
          </div>
        )}

        {/* Main Garden Interface */}
        <div className="bg-gradient-to-br from-green-400 to-green-500 rounded-xl shadow-lg p-6 relative overflow-hidden">
          {/* Task Planting Section */}
          <div className="mb-6">
            <h2 className="text-white text-xl font-semibold mb-4">Plant New Task</h2>
            <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
              <button
                onClick={() => handleTaskButtonClick('small')}
                className="bg-white bg-opacity-20 hover:bg-opacity-30 text-white p-4 rounded-lg flex flex-col items-center transition-all duration-200 transform hover:scale-105"
              >
                <span className="text-2xl mb-2">🌱</span>
                <span className="font-medium">Small Task</span>
                <span className="text-sm opacity-75">Quick & Simple</span>
              </button>
              <button
                onClick={() => handleTaskButtonClick('medium')}
                className="bg-white bg-opacity-20 hover:bg-opacity-30 text-white p-4 rounded-lg flex flex-col items-center transition-all duration-200 transform hover:scale-105"
              >
                <span className="text-2xl mb-2">🌿</span>
                <span className="font-medium">Medium Task</span>
                <span className="text-sm opacity-75">Moderate Effort</span>
              </button>
              <button
                onClick={() => handleTaskButtonClick('big')}
                className="bg-white bg-opacity-20 hover:bg-opacity-30 text-white p-4 rounded-lg flex flex-col items-center transition-all duration-200 transform hover:scale-105"
              >
                <span className="text-2xl mb-2">🌳</span>
                <span className="font-medium">Big Task</span>
                <span className="text-sm opacity-75">Major Project</span>
              </button>
            </div>
          </div>

          {/* Garden Display */}
          <div className="mb-6">
            <h3 className="text-white text-lg font-semibold mb-3">Your Garden</h3>
            <div className="bg-white bg-opacity-20 rounded-lg p-6 min-h-32 relative">
              {garden.length === 0 ? (
                <span className="text-white opacity-75 absolute inset-0 flex items-center justify-center">
                  Plant your first task to start growing!
                </span>
              ) : (
                <div className="grid grid-cols-3 sm:grid-cols-4 md:grid-cols-6 gap-4 place-items-center">
                  {garden.map((plant) => (
                    <div
                      key={plant.id}
                      className="relative group cursor-pointer transform transition-all duration-200 hover:scale-110 flex flex-col items-center"
                      onMouseEnter={() => setHoveredPlant(plant)}
                      onMouseLeave={() => setHoveredPlant(null)}
                    >
                      <div 
                        className={`p-2 rounded-lg border-2 ${statusConfig[plant.status].color} mb-2`}
                        onClick={() => {
                          const statuses = ['todo', 'inProgress', 'completed'];
                          const currentIndex = statuses.indexOf(plant.status);
                          const nextStatus = statuses[(currentIndex + 1) % statuses.length];
                          updateTaskStatus(plant.id, nextStatus);
                        }}
                      >
                        <div className="text-2xl animate-pulse">
                          {plant.icon}
                        </div>
                      </div>
                      <div className="text-xs text-white text-center max-w-16 truncate opacity-90">
                        {plant.description}
                      </div>
                      <div className="text-xs text-white opacity-75">
                        {statusConfig[plant.status].emoji}
                      </div>

                      {hoveredPlant && hoveredPlant.id === plant.id && (
                        <div className="absolute bottom-full left-1/2 transform -translate-x-1/2 mb-2 z-30">
                          <div className="bg-gray-800 text-white text-xs rounded px-3 py-2 whitespace-nowrap max-w-48 text-center">
                            <div className="font-medium">{plant.type.charAt(0).toUpperCase() + plant.type.slice(1)} Task</div>
                            <div className="opacity-75">{plant.description}</div>
                            <div className="text-yellow-300">{statusConfig[plant.status].label}</div>
                            <div className="text-gray-400 mt-1">Click to change status</div>
                            <div className="absolute top-full left-1/2 transform -translate-x-1/2 w-0 h-0 border-l-4 border-r-4 border-t-4 border-transparent border-t-gray-800"></div>
                          </div>
                        </div>
                      )}
                    </div>
                  ))}
                </div>
              )}
            </div>
          </div>

          {/* Harold the Gardener */}
          {showGardener && (
            <div 
              className="absolute transition-all duration-1000 ease-in-out z-10"
              style={{ 
                left: `${gardenerPosition.x}%`, 
                top: `${gardenerPosition.y}%`,
                filter: 'drop-shadow(0 2px 4px rgba(0,0,0,0.3))'
              }}
            >
              <div className={`text-3xl gardener-${gardenerEmotion}`}>
                {gardenerEmojis[gardenerEmotion]}
              </div>
              {chatMode === 'gardener' && (
                <div className="absolute -top-8 left-1/2 transform -translate-x-1/2">
                  <div className="bg-green-600 text-white text-xs px-2 py-1 rounded whitespace-nowrap">
                    Harold
                  </div>
                </div>
              )}
            </div>
          )}

          {/* Flying Bee */}
          <div 
            className="absolute transition-all duration-100 ease-linear z-10"
            style={{ 
              left: `${beePosition.x}%`, 
              top: `${beePosition.y}%`,
              transform: `scaleX(${beeDirection})`,
              filter: 'drop-shadow(0 2px 4px rgba(0,0,0,0.2))'
            }}
          >
            <div className={`text-2xl bee-flying ${beeEmotion === 'excited' ? 'bee-excited' : beeEmotion === 'sad' ? 'bee-sad' : ''}`}>
              {getBeeEmoji()}
            </div>
          </div>

          {/* AI Assistant Notification */}
          {showBee && (
            <div className="absolute top-4 right-4 bg-yellow-100 text-gray-800 p-3 rounded-lg shadow-lg max-w-xs animate-fade-in border border-yellow-200 z-20">
              <p className="text-sm">{beeMessage}</p>
            </div>
          )}

          {/* Chat Interface */}
          <div className="bg-white bg-opacity-95 rounded-lg p-4">
            <div className="mb-4">
              <div className="bg-gray-50 rounded-lg p-4 max-h-48 overflow-y-auto border">
                {chatMessages.length === 0 ? (
                  <p className="text-gray-500 text-center">
                    {chatMode === 'gardener' 
                      ? "Hello! I'm Harold, your garden assistant. How can I help you tend to your tasks today?" 
                      : "I'm here to help with your tasks and productivity. What are you working on?"}
                  </p>
                ) : (
                  <div className="space-y-3">
                    {chatMessages.slice(-6).map((msg, index) => (
                      <div key={index} className={`flex ${msg.role === 'user' ? 'justify-end' : 'justify-start'}`}>
                        <div className={`max-w-xs lg:max-w-md px-4 py-2 rounded-lg ${
                          msg.role === 'user' 
                            ? 'bg-blue-500 text-white' 
                            : msg.role === 'gardener'
                              ? 'bg-green-100 text-gray-800 border border-green-200'
                              : msg.isCheckIn 
                                ? 'bg-yellow-100 text-gray-800 border border-yellow-200'
                                : 'bg-gray-200 text-gray-800'
                        }`}>
                          <div className="text-sm">{msg.content}</div>
                        </div>
                      </div>
                    ))}
                  </div>
                )}
              </div>
            </div>

            <div className="flex gap-2">
              <input
                type="text"
                value={inputMessage}
                onChange={(e) => setInputMessage(e.target.value)}
                onKeyPress={handleKeyPress}
                placeholder={chatMode === 'gardener' 
                  ? "Ask Harold about gardening, tasks, or just chat..." 
                  : "Describe your task, ask for help, or just chat..."}
                className="flex-1 p-3 bg-white border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                disabled={isLoading}
              />
              <button
                onClick={handleSendMessage}
                disabled={isLoading || !inputMessage.trim()}
                className={`px-6 py-3 rounded-lg transition-colors font-medium ${
                  chatMode === 'gardener'
                    ? 'bg-green-500 hover:bg-green-600 disabled:bg-gray-400 text-white'
                    : 'bg-blue-500 hover:bg-blue-600 disabled:bg-gray-400 text-white'
                }`}
              >
                {isLoading ? '...' : 'Send'}
              </button>
            </div>
          </div>
        </div>

        {/* Chat History */}
        {chatMessages.length > 0 && (
          <div className="mt-6 bg-white rounded-xl shadow-lg p-6 border border-gray-200">
            <h3 className="text-lg font-semibold mb-4 text-gray-800">Conversation History</h3>
            <div className="space-y-3 max-h-96 overflow-y-auto">
              {chatMessages.map((msg, index) => (
                <div key={index} className={`p-4 rounded-lg ${
                  msg.role === 'user' 
                    ? 'bg-blue-50 border-l-4 border-blue-500' 
                    : msg.role === 'gardener'
                      ? 'bg-green-50 border-l-4 border-green-500'
                      : msg.isCheckIn
                        ? 'bg-yellow-50 border-l-4 border-yellow-500'
                        : 'bg-gray-50 border-l-4 border-gray-300'
                }`}>
                  <div className="font-medium text-sm mb-1 text-gray-600">
                    {msg.role === 'user' ? 'You' : 
                     msg.role === 'gardener' ? 'Harold the Gardener' :
                     msg.isCheckIn ? 'Assistant (Check-in)' : 'Assistant'}
                  </div>
                  <div className="text-gray-800">{msg.content}</div>
                </div>
              ))}
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
