// src/App.js
import React, { useState, useEffect, useRef } from 'react';
import './App.css';

const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000';

function App() {
  const [garden, setGarden] = useState([]);
  const [chatMessages, setChatMessages] = useState([]);
  const [inputMessage, setInputMessage] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [showBee, setShowBee] = useState(false);
  const [beeMessage, setBeeMessage] = useState('');
  const userId = 'default';
  const [beePosition, setBeePosition] = useState({ x: 20, y: 20 });
  const [beeDirection, setBeeDirection] = useState(1);
  const [beeEmotion, setBeeEmotion] = useState('happy');
  const [lastGardenSize, setLastGardenSize] = useState(0);
  const checkInTimerRef = useRef(null);
  const lastCheckInRef = useRef(Date.now());
  const beeTimerRef = useRef(null);
  const beeEmotionTimerRef = useRef(null);

  // Bee animation and behavior
  useEffect(() => {
    // Start bee animation
    beeTimerRef.current = setInterval(() => {
      setBeePosition(prev => {
        let newX = prev.x + (beeDirection * 2);
        let newY = prev.y + (Math.sin(newX * 0.1) * 0.5);

        // Bounce off walls
        if (newX > 85 || newX < 5) {
          setBeeDirection(d => d * -1);
          newX = prev.x;
        }

        return { x: newX, y: Math.max(5, Math.min(85, newY)) };
      });
    }, 100);

    // Check garden growth for bee emotions
    beeEmotionTimerRef.current = setInterval(() => {
      if (garden.length > lastGardenSize) {
        setBeeEmotion('excited');
        const compliments = [
          "Your garden is looking fantastic! ðŸŒŸ",
          "I love seeing your productivity bloom! ðŸŒ¸",
          "Such beautiful growth happening here! ðŸŒ±",
          "Your hard work is really paying off! âœ¨",
          "This garden is thriving thanks to you! ðŸŒ¿"
        ];
        showBeeCompanion(compliments[Math.floor(Math.random() * compliments.length)]);
        setLastGardenSize(garden.length);
      } else if (garden.length === lastGardenSize && garden.length > 0) {
        // Garden hasn't grown in a while
        setBeeEmotion('content');
      } else if (garden.length === 0) {
        setBeeEmotion('sad');
        if (Math.random() < 0.3) { // 30% chance to show sad message
          showBeeCompanion("The garden looks a bit empty... ready to plant something new? ðŸŒ±");
        }
      }
    }, 15000); // Check every 15 seconds

    return () => {
      clearInterval(beeTimerRef.current);
      clearInterval(beeEmotionTimerRef.current);
    };
  }, [garden.length, lastGardenSize]);

  const getBeeEmoji = () => {
    switch (beeEmotion) {
      case 'excited': return 'ðŸâœ¨';
      case 'sad': return 'ðŸðŸ˜”';
      case 'content': return 'ðŸðŸ’­';
      default: return 'ðŸ';
    }
  };

  // Plant icons for different task sizes
  const plantIcons = {
    small: 'ðŸŒ±',
    medium: 'ðŸŒ¿',
    big: 'ðŸŒ³'
  };

  // Load garden and chat history on component mount
  useEffect(() => {
    loadGarden();
    loadChatHistory();

    // Start periodic check-ins
    startCheckInTimer();

    // Cleanup timer on unmount
    return () => {
      if (checkInTimerRef.current) {
        clearInterval(checkInTimerRef.current);
      }
    };
  }, []);

  const startCheckInTimer = () => {
    // Check in every 60 seconds (1 minute)
    checkInTimerRef.current = setInterval(() => {
      const timeSinceLastMessage = Date.now() - lastCheckInRef.current;
      // Only check in if it's been more than 1 minute since last interaction
      if (timeSinceLastMessage > 60000) {
        initiateCheckIn();
      }
    }, 60000);
  };

  const initiateCheckIn = () => {
    const checkInMessages = [
      "How's your progress going?",
      "Need any help with your tasks?",
      "How are you feeling about your current work?",
      "Want to talk through anything you're working on?",
      "How's your focus today?"
    ];

    const randomMessage = checkInMessages[Math.floor(Math.random() * checkInMessages.length)];

    setChatMessages(prev => [...prev, {
      role: 'assistant',
      content: randomMessage,
      isCheckIn: true
    }]);

    lastCheckInRef.current = Date.now();
  };

  const loadGarden = async () => {
    try {
      const response = await fetch(`${API_BASE_URL}/api/garden/${userId}`);
      const data = await response.json();
      setGarden(data.garden);
    } catch (error) {
      console.error('Error loading garden:', error);
    }
  };

  const loadChatHistory = async () => {
    try {
      const response = await fetch(`${API_BASE_URL}/api/chat-history/${userId}`);
      const data = await response.json();
      setChatMessages(data.history);
    } catch (error) {
      console.error('Error loading chat history:', error);
    }
  };

  const showBeeCompanion = (message) => {
    setBeeMessage(message);
    setShowBee(true);
    setTimeout(() => setShowBee(false), 3000);
  };

  const plantTask = async (taskSize) => {
    try {
      const response = await fetch(`${API_BASE_URL}/api/garden/${userId}/plant`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ taskSize }),
      });

      const data = await response.json();
      setGarden(data.garden);
      showBeeCompanion(`Nice! Added a ${taskSize} task to your garden`);
    } catch (error) {
      console.error('Error planting task:', error);
    }
  };

  const waterGarden = () => {
    showBeeCompanion("Garden refreshed!");
  };

  const determineTaskSize = (message) => {
    const lowerMessage = message.toLowerCase();

    // Keywords that suggest task complexity
    const smallKeywords = ['quick', 'small', 'easy', 'simple', 'brief', 'short', 'minor', 'little'];
    const bigKeywords = ['big', 'large', 'major', 'complex', 'difficult', 'project', 'long', 'extensive', 'challenging'];

    // Time indicators
    const timeIndicators = {
      small: ['minute', 'minutes', '5 min', '10 min', '15 min'],
      medium: ['hour', 'hours', '1 hour', '2 hours', 'afternoon'],
      big: ['day', 'days', 'week', 'weeks', 'month']
    };

    let smallScore = 0;
    let bigScore = 0;

    // Check for keywords
    smallKeywords.forEach(keyword => {
      if (lowerMessage.includes(keyword)) smallScore++;
    });

    bigKeywords.forEach(keyword => {
      if (lowerMessage.includes(keyword)) bigScore++;
    });

    // Check for time indicators
    Object.entries(timeIndicators).forEach(([size, indicators]) => {
      indicators.forEach(indicator => {
        if (lowerMessage.includes(indicator)) {
          if (size === 'small') smallScore += 2;
          else if (size === 'big') bigScore += 2;
        }
      });
    });

    // Determine size based on scores
    if (smallScore > bigScore) return 'small';
    if (bigScore > smallScore) return 'big';
    return 'medium'; // default
  };

  const generateResponse = async (message) => {
    // Use the actual API for better responses
    const response = await fetch(`${API_BASE_URL}/api/chat`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ message, userId }),
    });

    const data = await response.json();

    // Check if user wants to add a task and auto-plant it
    const lowerMessage = message.toLowerCase();
    if (lowerMessage.includes('task') || lowerMessage.includes('todo') || lowerMessage.includes('need to') || lowerMessage.includes('should')) {
      const taskSize = determineTaskSize(message);
      plantTask(taskSize);
    }

    return data.response || data.message || "I'm here to help! What would you like to talk about?";
  };

  const getEncouragement = () => {
    const encouragements = [
      "You're making good progress.",
      "Keep up the momentum.",
      "Nice work staying organized.",
      "You're building good habits.",
      "That's progress worth noting."
    ];
    return encouragements[Math.floor(Math.random() * encouragements.length)];
  };

  const sendMessageToAI = async (message) => {
    setIsLoading(true);
    lastCheckInRef.current = Date.now(); // Reset check-in timer

    try {
      const response = await generateResponse(message);

      setChatMessages(prev => [...prev, 
        { role: 'user', content: message },
        { role: 'assistant', content: response }
      ]);
    } catch (error) {
      console.error('Error sending message:', error);
      setChatMessages(prev => [...prev,
        { role: 'user', content: message },
        { role: 'assistant', content: "I'm having trouble right now, but I'm here to help. Try again in a moment." }
      ]);
    } finally {
      setIsLoading(false);
    }
  };

  const handleSendMessage = () => {
    if (inputMessage.trim()) {
      sendMessageToAI(inputMessage);
      setInputMessage('');
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      handleSendMessage();
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 p-4">
      <div className="max-w-4xl mx-auto">
        {/* Header */}
        <div className="bg-white rounded-xl shadow-lg p-6 mb-6 border border-gray-200">
          <h1 className="text-3xl font-bold text-gray-900 mb-3">Task Garden</h1>
          <p className="text-gray-600 leading-relaxed">
            Plant tasks of different sizes and watch your productivity garden grow. 
            Chat with your AI assistant for help, task planning, and regular check-ins.
          </p>
        </div>

        {/* Main Garden Interface */}
        <div className="bg-gradient-to-br from-green-400 to-green-500 rounded-xl shadow-lg p-6 relative overflow-hidden">
          {/* Task Planting Section */}
          <div className="mb-6">
            <h2 className="text-white text-xl font-semibold mb-4">Plant New Task</h2>
            <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
              <button
                onClick={() => plantTask('small')}
                className="bg-white bg-opacity-20 hover:bg-opacity-30 text-white p-4 rounded-lg flex flex-col items-center transition-all duration-200 transform hover:scale-105"
              >
                <span className="text-2xl mb-2">ðŸŒ±</span>
                <span className="font-medium">Small Task</span>
                <span className="text-sm opacity-75">Quick & Simple</span>
              </button>
              <button
                onClick={() => plantTask('medium')}
                className="bg-white bg-opacity-20 hover:bg-opacity-30 text-white p-4 rounded-lg flex flex-col items-center transition-all duration-200 transform hover:scale-105"
              >
                <span className="text-2xl mb-2">ðŸŒ¿</span>
                <span className="font-medium">Medium Task</span>
                <span className="text-sm opacity-75">Moderate Effort</span>
              </button>
              <button
                onClick={() => plantTask('big')}
                className="bg-white bg-opacity-20 hover:bg-opacity-30 text-white p-4 rounded-lg flex flex-col items-center transition-all duration-200 transform hover:scale-105"
              >
                <span className="text-2xl mb-2">ðŸŒ³</span>
                <span className="font-medium">Big Task</span>
                <span className="text-sm opacity-75">Major Project</span>
              </button>
            </div>
          </div>

          {/* Garden Display */}
          <div className="mb-6">
            <h3 className="text-white text-lg font-semibold mb-3">Your Garden</h3>
            <div className="bg-white bg-opacity-20 rounded-lg p-4 min-h-24 flex flex-wrap gap-2">
              {garden.length === 0 ? (
                <span className="text-white opacity-75">Plant your first task to start growing!</span>
              ) : (
                garden.map((plant) => (
                  <span key={plant.id} className="text-2xl animate-pulse">
                    {plantIcons[plant.type]}
                  </span>
                ))
              )}
            </div>
          </div>

          {/* Flying Bee */}
          <div 
            className="absolute transition-all duration-100 ease-linear z-10"
            style={{ 
              left: `${beePosition.x}%`, 
              top: `${beePosition.y}%`,
              transform: `scaleX(${beeDirection})`,
              filter: 'drop-shadow(0 2px 4px rgba(0,0,0,0.2))'
            }}
          >
            <div className={`text-2xl bee-flying ${beeEmotion === 'excited' ? 'bee-excited' : beeEmotion === 'sad' ? 'bee-sad' : ''}`}>
              {getBeeEmoji()}
            </div>
          </div>

          {/* AI Assistant Notification */}
          {showBee && (
            <div className="absolute top-4 right-4 bg-yellow-100 text-gray-800 p-3 rounded-lg shadow-lg max-w-xs animate-fade-in border border-yellow-200 z-20">
              <p className="text-sm">{beeMessage}</p>
            </div>
          )}

          {/* Chat Interface */}
          <div className="bg-white bg-opacity-95 rounded-lg p-4">
            <div className="mb-4">
              <div className="bg-gray-50 rounded-lg p-4 max-h-48 overflow-y-auto border">
                {chatMessages.length === 0 ? (
                  <p className="text-gray-500 text-center">I'm here to help with your tasks and check in regularly. What are you working on?</p>
                ) : (
                  <div className="space-y-3">
                    {chatMessages.slice(-6).map((msg, index) => (
                      <div key={index} className={`flex ${msg.role === 'user' ? 'justify-end' : 'justify-start'}`}>
                        <div className={`max-w-xs lg:max-w-md px-4 py-2 rounded-lg ${
                          msg.role === 'user' 
                            ? 'bg-blue-500 text-white' 
                            : msg.isCheckIn 
                              ? 'bg-yellow-100 text-gray-800 border border-yellow-200'
                              : 'bg-gray-200 text-gray-800'
                        }`}>
                          <div className="text-sm">{msg.content}</div>
                        </div>
                      </div>
                    ))}
                  </div>
                )}
              </div>
            </div>

            <div className="flex gap-2">
              <input
                type="text"
                value={inputMessage}
                onChange={(e) => setInputMessage(e.target.value)}
                onKeyPress={handleKeyPress}
                placeholder="Describe your task, ask for help, or just chat..."
                className="flex-1 p-3 bg-white border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                disabled={isLoading}
              />
              <button
                onClick={handleSendMessage}
                disabled={isLoading || !inputMessage.trim()}
                className="bg-blue-500 hover:bg-blue-600 disabled:bg-gray-400 text-white px-6 py-3 rounded-lg transition-colors font-medium"
              >
                {isLoading ? '...' : 'Send'}
              </button>
            </div>
          </div>
        </div>

        {/* Chat History */}
        {chatMessages.length > 0 && (
          <div className="mt-6 bg-white rounded-xl shadow-lg p-6 border border-gray-200">
            <h3 className="text-lg font-semibold mb-4 text-gray-800">Conversation History</h3>
            <div className="space-y-3 max-h-96 overflow-y-auto">
              {chatMessages.map((msg, index) => (
                <div key={index} className={`p-4 rounded-lg ${
                  msg.role === 'user' 
                    ? 'bg-blue-50 border-l-4 border-blue-500' 
                    : msg.isCheckIn
                      ? 'bg-yellow-50 border-l-4 border-yellow-500'
                      : 'bg-gray-50 border-l-4 border-gray-300'
                }`}>
                  <div className="font-medium text-sm mb-1 text-gray-600">
                    {msg.role === 'user' ? 'You' : msg.isCheckIn ? 'Assistant (Check-in)' : 'Assistant'}
                  </div>
                  <div className="text-gray-800">{msg.content}</div>
                </div>
              ))}
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
